import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../assets/styles/login.css'; // Import the CSS file

export const Login = () => {
  const navigate = useNavigate();

  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [isAgreed, setIsAgreed] = useState(false);

  const handleLogin = () => {
    if (!userId) {
      alert('Please enter your username.');
      return;
    }

    if (!password) {
      alert('Please enter your password.');
      return;
    }

    if (!isAgreed) {
      alert('Please accept the terms and conditions.');
      return;
    }

    sessionStorage.setItem("userId", userId);
    sessionStorage.setItem("loginToken", "log123");
    navigate('/home');
  };

  return (
    <div className="container login-container">
      <h1 className="heading">EZPay</h1> {/* Large heading for the app */}
      <h2 className="subheading">SECURE AND EFFICIENT DIGITAL PAYMENT SOLUTION</h2> {/* Subheading added */}

      <div className="row justify-content-center">
        <div className="login-box">
          <h2 className="login-header">Login Page</h2>

          <div className="form-group mb-3">
            <label className="form-label">Username</label>
            <input
              type="text"
              className="form-control"
              value={userId}
              onChange={(e) => setUserId(e.target.value)}
              placeholder="Enter Username"
            />
          </div>

          <div className="form-group mb-3">
            <label className="form-label">Password</label>
            <input
              type="password"
              className="form-control"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter Password"
            />
          </div>

          <div className="form-check mb-3">
            <input
              type="checkbox"
              className="form-check-input"
              checked={isAgreed}
              onChange={(e) => setIsAgreed(e.target.checked)}
              id="agreementCheck"
            />
            <label className="form-check-label" htmlFor="agreementCheck">
              I accept the terms and conditions
            </label>
          </div>

          <button className="btn login-btn" onClick={handleLogin}>
            Login
          </button>
        </div>
      </div>
    </div>
  );
};
