import "../assets/styles/Reminder.css";

function Reminder(props) {

    return (
        <div className="reminder">
            <div className="rem-point">
                <button className="delete-reminder-button" onClick={(e) => { handleDeleteReminder(props.rem.reminderId) }}>
                    <i class="fa fa-trash-o"></i>
                </button>
                <label className="rem-text">
                    Payment of <b>&#8377; {props.rem.amount}</b> is due on <b>{props.rem.dueDate.toLocaleString('en-GB', {
                        day: "2-digit",
                        month: "2-digit",
                        year: "numeric"
                    })}</b>.
                </label>
            </div>
        </div>
    )

}

export default Reminder;
