import Header from "./Header";
import ReminderTitle from "./ReminderTitle";
import "../assets/styles/PaymentReminder.css";
import { Modal } from "react-bootstrap";
import { useState } from "react";

const PaymentReminder = () => {
    const reminders = [
        {reminderId:"R01U01", userId:"U01", amount:1500, dueDate:new Date("2024-09-07")},
        {reminderId:"R02U01", userId:"U01", amount:500, dueDate:new Date("2024-09-08")},
        {reminderId:"R03U01", userId:"U01", amount:1000, dueDate:new Date("2024-09-06")},
        {reminderId:"R04U01", userId:"U01", amount:2500, dueDate:new Date("2024-09-07")}
    ];

    const [showAddForm, setShowAddForm] = useState(false);
    const handleAddReminder = () => {
        setShowAddForm(!showAddForm);
    }

    return (
        <>
            <Header />
            <ReminderTitle />
            {
                reminders.map((rem, index) => {
                    return (
                        <div className="reminder">
                            <div className="rem-point">
                                <input type="checkbox" name={`reminder_${index}`} />
                                <label for={`reminder_${index}`} className="rem-text">
                                    Payment of <b>&#8377; {rem.amount}</b> is due on <b>{rem.dueDate.toLocaleString('en-GB', {
                                        day:"2-digit",
                                        month:"2-digit",
                                        year:"numeric"
                                    })}</b>.
                                    </label>
                            </div>
                        </div>
                    )
                })
            }
            <div className="add-pr-div">
                <div className="add-pr-button-container">
                    <button className="add-pr-button" onClick={handleAddReminder}>
                        +
                    </button>
                </div>
                <div className="add-pr-form-container" style={(showAddForm) ? {height:"40vh", "border-width":"0"} : null}>
                    <div className="add-pr-form-title">

                    </div>
                </div>
            </div>
        </>
    );
}

export default PaymentReminder;
