import Header from "./Header";
import ReminderTitle from "./ReminderTitle";
import "../assets/styles/PaymentReminder.css";
import { Modal } from "react-bootstrap";
import { useEffect, useRef, useState } from "react";

// const reminders = [
//     {reminderId:"R01U01", userId:"U01", amount:1500, dueDate:new Date("2024-09-07")},
//     {reminderId:"R02U01", userId:"U01", amount:500, dueDate:new Date("2024-09-08")},
//     {reminderId:"R03U01", userId:"U01", amount:1000, dueDate:new Date("2024-09-06")},
//     {reminderId:"R04U01", userId:"U01", amount:2500, dueDate:new Date("2024-09-07")}
// ];

const PaymentReminder = () => {
    
    const fetchPaymentReminders = async () => {
        let response = await fetch("http://localhost:7000/api/payment-reminders/fetch/U01", {"method":"GET"});
        let reminders = await response.json();
        // console.log(reminders);
        setReminderList(reminders);
    }

    fetchPaymentReminders();

    const [reminderList, setReminderList] = useState(reminders);
    const [showAddForm, setShowAddForm] = useState(false);
    
    const handleAddReminderForm = () => {
        setShowAddForm(!showAddForm);
    }

    const handleDeleteReminder = (reminderId) => {
        let newReminderList = reminders.filter(rem => {return rem.reminderId !== reminderId});
        setReminderList(newReminderList);
    }

    const amountRef = useRef();
    const dueDateRef = useRef();
    const statusRef = useRef();

    const handleAddReminder = () => {
        let reminder = {
            reminderId: (Math.random() * 100).toString(),
            userId: "U01",
            amount: Number(amountRef.current.value),
            dueDate: new Date(dueDateRef.current.value),
            status: statusRef.current.value
        };
        setReminderList(reminders => [...reminders, reminder]);
    }

    return (
        <>
            <Header />
            <ReminderTitle />
            {
                reminderList.map((rem, index) => {
                    return (
                        <div className="reminder">
                            <div className="rem-point">
                                <button className="delete-reminder-button" onClick={(e) => {handleDeleteReminder(rem.reminderId)}}>
                                    <i class="fa fa-trash-o"></i>
                                </button>
                                {/* <img src="../assets/images/alarm_icon.png" /> */}
                                <label for={`reminder_${index}`} className="rem-text">
                                    Payment of <b>&#8377; {rem.amount}</b> is due on <b>{rem.dueDate.toLocaleString('en-GB', {
                                        day:"2-digit",
                                        month:"2-digit",
                                        year:"numeric"
                                    })}</b>.
                                    </label>
                            </div>
                        </div>
                    )
                })
            }
            <div className="add-pr-div">
                <div className="add-pr-button-container">
                    <button className="add-pr-button" onClick={handleAddReminderForm}>
                        +
                    </button>
                </div>
                <div className={"add-pr-form-container " + ((showAddForm) ? "display-form" : null)}>
                    <div className="add-pr-form-title">
                        <h3>Add Payment Reminder</h3>
                    </div>
                    <div className="add-pr-form">
                        <div className="form-field">
                            <p>Payment amount:</p>
                            <input type="text" placeholder="Enter amount" ref={amountRef} />
                        </div>
                        <div className="form-field">
                            <p>Due date:</p>
                            <input type="text" placeholder="(YYYY-MM-DD)" ref={dueDateRef} />
                        </div>
                        <div className="form-field">
                            <p>Status:</p>
                            <input type="text" placeholder="Enter status" ref={statusRef} />
                        </div>
                        <div className="form-field">
                            <div className="form-button-container">
                                <button onClick={(e) => {handleAddReminder()}}>
                                    Add Reminder
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default PaymentReminder;
